  let answer = 0;
  const point = [];
  for (let i = 0; i < A.length; i++) {
    const [start, end] = [i - A[i], i + A[i]];
    point.push([start, end]);
  }

  point.sort((a, b) => a[0] - b[0] || b[1] - a[1]);

  for (let i = 0; i < point.length; i++) {
    for (let j = i + 1; j < point.length; j++) {
      if (point[i][1] < point[j][0]) continue;
      answer++;
    }
  }
  return answer;
}
