`,(0,a.jsxDEV)(r.pre,{children:(0,a.jsxDEV)(r.code,{className:"language-js",children:`const TodoContainer = () => {
  const { data, isError, isPending } = getTodoApi();

  if (isPending) {
    return <div>loading...</div>;
  }

  return (
    <main className="flex h-screen w-full flex-col justify-center">
      <div className="mx-auto flex w-[600px] flex-col gap-5">
        <div className="flex justify-end">
          <ServerInputModal />
        </div>
        {!isError && data && (
          <>
            {data.map(({ content, completed, id }) => (
              <TodoItem
                checked={completed}
                todo={content}
                todoId={id}
                key={\`\${content}-\${id}\`}
              />
            ))}
          </>
        )}
        {isError && <AlertBox />}
      </div>
    </main>
  );
};

export default TodoContainer;
