  const { data, isError, isPending } = getTodoApi();

  if (isPending) {
    return <div>loading...</div>;
  }

  return (
    <main className="flex h-screen w-full flex-col justify-center">
      <div className="mx-auto flex w-[600px] flex-col gap-5">
